import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { Observable } from 'rxjs';
import { FoodService } from '../food.service';
import { Location } from '@angular/common';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
  })

export class ItemComponent implements OnInit {
  routeObs: Observable<ParamMap> | undefined;
  foodServiceObs: Observable<Object> | undefined;
  x = 0;
  mostra=false;
  carrello : string [] = [];
  carrelloQ : string [] = [];

  items: any; //Qui salverò la traccia selezionata
  isDisabled: boolean | undefined;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private service: FoodService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.routeObs = this.route.paramMap;
    this.routeObs.subscribe(this.getRouterParam);

    if (this.x >= 1) {
      this.isDisabled = false;
    } else {
      this.isDisabled = true;
    }
  }

  getRouterParam = (params: ParamMap) => {
    console.log(params);
    let itemId = params.get('id'); 
    console.log(itemId); //Stampo su console
    this.foodServiceObs = this.service.searchId(itemId);
    this.foodServiceObs.subscribe(
      (data) => ((this.items = data), console.log(data))
    );
  };

  aggiungi = () => {
    this.x += 1;

    if (this.x >= 1) {
      this.isDisabled = false;
    } else {
      this.isDisabled = true;
    }
  }

  sottrai = () => {
    if (this.x > 0) {
      this.x -= 1
    }

    if (this.x >= 1) {
      this.isDisabled = false;
    } else {
      this.isDisabled = true;
    }
  }

  mostraDiv = (nomeProd: any) => {
    this.mostra = true;

    let xStringato = this.x.toString();
    localStorage.setItem(nomeProd, xStringato);

    for (var i = 0; i < localStorage.length; i++){
      let key =  localStorage.key(i);
      if (key != null) {
        let val = localStorage.getItem(key)
          if(val != null) {
            this.carrello.push(key);
            this.carrelloQ.push(val);
          }
      }
    }  

  }

  goBack(): void {
    this.location.back();
  }

  rimuoviEl = () => {
    let y=localStorage.length;
    y=y-1;

    this.carrello.splice(localStorage.length-1, 1);
    this.carrelloQ.splice(localStorage.length-1, 1);

    let key =  localStorage.key(y);
    if (key != null) {
      let val = localStorage.getItem(key);
      if(val != null) {
        localStorage.removeItem(key);
      }
    }

    console.log(localStorage) //Stampo su console
    console.log(this.carrello) //Stampo su console
  }

  goToPage(): void {
    this.router.navigate(['/ordine']);
  }
}
<button type="button" class="btn btn-secondary text-dark" (click)="goBack()" style="margin-left: 20px; margin-top: 20px; float: left">
  Indietro</button>

<div style="width: 50%; margin: auto;">

  <div *ngIf="items" class="card" style="width: 300px; margin-left: 20px; margin-top: 20px; float: left">

    <img class="card-img-top" src="{{items.product.image_url}}" alt="Card image" style="float: left">

    <div class="card-body col-sm-12 col-lg-12">
      <p class="card-text">- Nome: {{items.product.product_name}}</p>
      <p class="card-text">- Marca: {{items.product.brands}}</p>
      <p class="card-text">- Peso: {{items.product.quantity}}</p>
      <p class="card-text">- Grassi: {{items.product.nutrient_levels.fat}}</p>
      <p class="card-text">- Sale: {{items.product.nutrient_levels.salt}}</p>
      <p class="card-text">- Zuccheri: {{items.product.nutrient_levels.sugars}}</p>
    </div>

  </div>

  <div *ngIf="items" class="card" style="width: 300px; margin-left: 20px; margin-top: 20px; float: left">

    <div class="card-body col-sm-12 col-lg-12">
      <h5>Quantità:</h5>
      <button type="button" class="btn btn-secondary text-dark" (click)="aggiungi()">+</button>
      <label style="margin-left: 10px; margin-right: 10px"> {{ x }} </label>
      <button type="button" class="btn btn-secondary text-dark" (click)="sottrai()">-</button><br>
      <br><button type="button" class="btn btn-secondary text-dark" (click)="mostraDiv(items.product.product_name)" 
      [disabled]="isDisabled" disabled>aggiungi al carrello</button>
    </div>

  </div>

  <div *ngIf="mostra" class="card" style="width: 300px; margin-left: 20px; margin-top: 20px; float: left">

    <div class="card-body col-sm-12 col-lg-12" >
      <h2>Carrello</h2><br>
      <div *ngFor="let c of carrello">
        <p class="card-text"><mark>{{ c }}</mark></p><br>
      </div>
      <div *ngFor="let c of carrelloQ">
        <p class="card-text"> X {{ c }}</p><br>
      </div>
      <br><button type="button" class="btn btn-secondary text-dark" (click)="rimuoviEl()">
      rimuovi elemento</button><br>
      <br><button type="button" class="btn btn-secondary text-dark" (click)="goToPage()">conferma ordine</button>
    </div>

  </div>

</div>
