import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { FoodService } from '../food.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css'],
})
export class SearchComponent implements OnInit {
  query: string | undefined;
  obsProduct: Observable<Object> | undefined;
  results: any;

  constructor(public food: FoodService) {}

  ngOnInit(): void {}

  submit(query: HTMLInputElement): void {
    if (!query.value) {
      return;
    }
    this.query = query.value;
    this.obsProduct = this.food.searchProduct(this.query);
    this.obsProduct.subscribe((data) => {
      this.results = data;
      console.log(this.results);
    });
  }

  renderResults(res: any): void {
    this.results = null;
    if (res && res.tracks && res.tracks.items) {
      this.results = res.tracks.items;
    }
  }
}
<div>

    <form class="d-flex" style="margin-left: 20%; margin-right: 20%; margin-top: 5%; margin-bottom: 5%">
        <input class="form-control me-2" type="text" placeholder="Search" #query>
        <button type="button" class="btn btn-secondary text-dark" (click)="submit(query)">Search</button>
    </form>

    <div *ngIf="results" class="bg-dark text-bg-danger">
        <div class="row">
            <div class="col-sm-6 col-md-4" *ngFor="let t of results.products">
                <div class="card" style="width: 250px; margin-left: 20px; margin-top: 20px;">
                    <img src="{{t.image_url}}" class="img-fluid" alt="">
                    <div class="card-body">
                        <p>{{t.brands}}</p>
                        <a [routerLink]="['/item', t.id]">
                            {{ t.id }}
                        </a>
                    </div>
                </div>
            </div>
        </div>
    </div>

</div>
